import './App.css';

import React, {useState} from 'react';

function App() {

    // first valeue, next function to change the state value
    const [count,setCount] = useState(4)
    const [theme,setTheme] = useState('Click on the button to change')

    function decCount() {
        setCount(count - 1)
        setTheme('Removed')
    }

    function inCount() {
        setCount(count + 1)
        setTheme('Add')
    }

    return (
        <div className="App">

            <button onClick={decCount}>-</button>
            <span>{count}</span>
            <button onClick={inCount}>+</button>

            <span>{theme}</span>

        </div>
    );
}

export default App;
